contracts/SaraDEX.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./SaraLiquidityManager.sol";

contract SaraDEX is Ownable, AccessControl {
    using SafeERC20 for IERC20;

    // Add role identifier for liquidity managers
    bytes32 public constant LIQUIDITY_MANAGER_ROLE = keccak256("LIQUIDITY_MANAGER_ROLE");

    address public coralToken; // Address of Coral's native token (CORAL)
    SaraLiquidityManager public liquidityManager;
    uint256 public swapFee = 30; // 0.3% fee (divided by 10,000 for precision)

    // Add anti-bot protection
    mapping(address => uint256) public lastSwapTimestamp;
    uint256 public constant MIN_TIME_BETWEEN_SWAPS = 1 minutes;
    uint256 public constant MAX_SWAP_AMOUNT_PERCENT = 5; // 5% of reserves

    // Add fee storage tracking
    mapping(address => uint256) public storedFees;

    uint256 public constant MAX_SINGLE_SWAP = 1000 * 1e18; // 1000 tokens per swap

    // Add threshold for auto fee redeployment
    uint256 public constant AUTO_REDEPLOY_THRESHOLD = 1000 * 1e18; // 1000 tokens

    // Change from constant to immutable/variable
    uint256 public immutable DEFAULT_MAX_SLIPPAGE = 500; // 5%
    uint256 public ABSOLUTE_MAX_SLIPPAGE = 1000; // 10% initial cap
    uint256 public constant MAXIMUM_ALLOWED_SLIPPAGE = 2000; // 20% absolute maximum

    // Add error messages as constants
    string private constant ERR_SAME_VALUE = "New slippage same as current";
    string private constant ERR_LOW_SLIPPAGE = "Cannot be lower than default";
    string private constant ERR_HIGH_SLIPPAGE = "Cannot exceed 20%";
    string private constant ERR_INSUFFICIENT_CORAL = "Insufficient CORAL token balance";
    string private constant ERR_INVALID_CORAL_AMOUNT = "Invalid CORAL token amount";
    string private constant ERR_FAILED_CORAL_TRANSFER = "Failed to get CORAL tokens from Liquidity Manager";
    string private constant ERR_CORAL_SWAP_FAILED = "CORAL swap execution failed";

    event CoralSwapExecuted(
        address indexed user,
        address indexed creatorToken,
        uint256 amountIn,
        uint256 amountOut,
        uint256 fee
    );

    event CoralFeesWithdrawn(
        address indexed token,
        uint256 amount,
        uint256 timestamp
    );

    // Add event for slippage updates
    event MaxSlippageUpdated(
        uint256 oldSlippage,
        uint256 newSlippage,
        address indexed updater
    );

    // Add event for failed auto-redeployments
    event CoralAutoRedeploymentFailed(address indexed token, uint256 storedFees);
    event CoralFeesRedeployed(address indexed token, uint256 amount);

    event CoralTokensRequested(address indexed token, uint256 amount, uint256 timestamp);

    // Add fee handling constants and documentation
    /**
     * @dev Constants for fee handling and auto-redeployment
     * AUTO_REDEPLOY_THRESHOLD: Minimum amount of fees before auto-redeployment (1000 CORAL)
     * MAX_FEE_STORAGE: Maximum amount of fees that can be stored (10000 CORAL)
     */
    uint256 public constant MAX_FEE_STORAGE = 10000 * 1e18; // 10000 CORAL tokens maximum storage

    // Add constants for minimum amounts
    uint256 public constant MIN_SWAP_AMOUNT = 1e15; // 0.001 tokens minimum input
    uint256 public constant MIN_OUTPUT_AMOUNT = 1e15; // 0.001 tokens minimum output

    constructor(
        address _coralToken, 
        address payable _liquidityManager
    ) Ownable(msg.sender) {
        coralToken = _coralToken;
        liquidityManager = SaraLiquidityManager(_liquidityManager);

        // Setup initial roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(LIQUIDITY_MANAGER_ROLE, _liquidityManager);
    }

    // Add modifier for liquidity manager role
    modifier onlyLiquidityManager() {
        require(hasRole(LIQUIDITY_MANAGER_ROLE, msg.sender), "Not a liquidity manager");
        _;
    }

    // Add fee update function
    function updateSwapFee(uint256 newFee) external onlyLiquidityManager {
        require(newFee <= 100, "Fee cannot exceed 1%");
        require(newFee != swapFee, "New fee same as current");
        uint256 oldFee = swapFee;
        swapFee = newFee;
        emit SwapFeeUpdated(oldFee, newFee, msg.sender);
    }

    function swapCreatorTokenForCoral(
        address creatorToken, 
        uint256 amountIn, 
        uint256 minAmountOut,
        uint256 maxSlippage
    ) external {
        // Anti-bot check
        require(block.timestamp >= lastSwapTimestamp[msg.sender] + MIN_TIME_BETWEEN_SWAPS, "Too many swaps");

        // Validate and set slippage
        require(maxSlippage <= ABSOLUTE_MAX_SLIPPAGE, "Slippage too high");
        uint256 slippageUsed = maxSlippage > 0 ? maxSlippage : DEFAULT_MAX_SLIPPAGE;
        require(slippageUsed >= DEFAULT_MAX_SLIPPAGE, "Invalid slippage");

        // Get reserves
        (uint256 reserveCreator, uint256 reserveCoral) = liquidityManager.getReserves(creatorToken);

        // Check both token liquidities
        require(amountIn <= reserveCreator, "Insufficient creator liquidity");
        require(
            hasTokenBalance(coralToken, amountIn),
            "No CORAL liquidity available"
        );

        // Large swap protection
        require(amountIn <= reserveCreator * MAX_SWAP_AMOUNT_PERCENT / 100, "Swap too large");

        // Calculate expected output with fees
        uint256 expectedOutput = getAmountOut(amountIn, reserveCreator, reserveCoral);
        uint256 fee = (expectedOutput * swapFee) / 10000;
        uint256 amountOutAfterFee = expectedOutput - fee;

        // Validate slippage with defined slippageUsed
        uint256 minAcceptableOutput = amountOutAfterFee - ((amountOutAfterFee * slippageUsed) / 10000);
        require(minAcceptableOutput >= minAmountOut, "Slippage too high");

        // Transfer creator tokens first (CEI pattern)
        require(
            IERC20(creatorToken).balanceOf(msg.sender) >= amountIn,
            "Insufficient creator token balance"
        );
        require(
            IERC20(creatorToken).allowance(msg.sender, address(this)) >= amountIn,
            "Insufficient creator token allowance"
        );
        IERC20(creatorToken).safeTransferFrom(msg.sender, address(this), amountIn);

        // Handle CORAL token transfer
        safeTransferCoral(msg.sender, amountOutAfterFee);

        // Store fees
        storeFees(creatorToken, fee);

        // Update state and emit event
        lastSwapTimestamp[msg.sender] = block.timestamp;
        emit CoralSwapExecuted(msg.sender, creatorToken, amountIn, amountOutAfterFee, fee);
    }

    function _executeCoralSwap(
        address creatorToken,
        uint256 amountIn,
        uint256 maxSlippage
    ) internal returns (uint256) {
        // Validate slippage
        require(maxSlippage <= ABSOLUTE_MAX_SLIPPAGE, "Slippage too high");
        uint256 slippageUsed = maxSlippage > 0 ? maxSlippage : DEFAULT_MAX_SLIPPAGE;
        require(slippageUsed >= DEFAULT_MAX_SLIPPAGE, "Invalid slippage");

        // Check creator token allowance
        require(
            IERC20(creatorToken).allowance(msg.sender, address(this)) >= amountIn,
            "Insufficient creator token allowance"
        );

        // Get reserves and validate liquidity
        (uint256 reserveCreator, uint256 reserveCoral) = liquidityManager.getReserves(creatorToken);
        require(amountIn <= reserveCreator, "Insufficient liquidity in pool");
        require(reserveCoral > amountIn, "Insufficient CORAL token liquidity");

        // Calculate output amounts
        uint256 expectedOutput = getAmountOut(amountIn, reserveCreator, reserveCoral);
        uint256 slippageAmount = (expectedOutput * slippageUsed) / 10000;
        uint256 minAcceptableOutput = expectedOutput - slippageAmount;

        // Calculate fees
        uint256 fee = (expectedOutput * swapFee) / 10000;
        uint256 amountOutAfterFee = expectedOutput - fee;

        // For large swaps, calculate per-swap minimum output
        if (amountIn > MAX_SINGLE_SWAP) {
            uint256 numSwaps = (amountIn + MAX_SINGLE_SWAP - 1) / MAX_SINGLE_SWAP;

            for (uint i = 0; i < numSwaps; i++) {
                uint256 currentSwapAmount = i == numSwaps - 1 ? 
                    amountIn - (i * MAX_SINGLE_SWAP) : 
                    MAX_SINGLE_SWAP;

                uint256 expectedPerSwap = (expectedOutput * currentSwapAmount) / amountIn;
                uint256 minPerSwap = expectedPerSwap - ((expectedPerSwap * slippageUsed) / 10000);

                uint256 receivedAmount = _executeSingleCoralSwap(creatorToken, currentSwapAmount);
                require(
                    receivedAmount >= minPerSwap,
                    "Per-swap slippage too high"
                );

                // Track total received amount
                amountOutAfterFee += receivedAmount;
            }

            // Verify total slippage after all swaps
            require(
                amountOutAfterFee >= minAcceptableOutput,
                "Total slippage too high"
            );
        } else {
            // For normal sized swaps, check total slippage
            require(amountOutAfterFee >= minAcceptableOutput, "Slippage too high");
        }

        // Ensure we have enough CORAL tokens for the swap
        ensureCoralBalance(amountOutAfterFee);

        // Checks (CEI Pattern)
        require(
            IERC20(creatorToken).balanceOf(msg.sender) >= amountIn,
            "Insufficient creator token balance"
        );
        require(
            IERC20(creatorToken).allowance(msg.sender, address(this)) >= amountIn,
            "Insufficient creator token allowance"
        );

        // Effects
        storeFees(creatorToken, fee);
        lastSwapTimestamp[msg.sender] = block.timestamp;

        // Interactions (in correct order)
        IERC20(creatorToken).safeTransferFrom(msg.sender, address(this), amountIn);
        safeTransferCoral(msg.sender, amountOutAfterFee);

        emit CoralSwapExecuted(msg.sender, creatorToken, amountIn, amountOutAfterFee, fee);
        return amountOutAfterFee;
    }

    function _executeSingleCoralSwap(
        address creatorToken,
        uint256 swapAmount
    ) internal returns (uint256) {
        // Get reserves
        (uint256 reserveCreator, uint256 reserveCoral) = liquidityManager.getReserves(creatorToken);

        // Calculate output
        uint256 expectedOutput = getAmountOut(swapAmount, reserveCreator, reserveCoral);
        uint256 fee = (expectedOutput * swapFee) / 10000;
        uint256 amountOutAfterFee = expectedOutput - fee;

        // Store fees
        storeFees(creatorToken, fee);

        return amountOutAfterFee;
    }

    /**
     * @dev Calculates the output amount for a swap
     * @param amountIn Amount of input tokens
     * @param reserveIn Reserve of input tokens
     * @param reserveOut Reserve of output tokens
     * @return uint256 Amount of output tokens after fees
     */
    function getAmountOut(
        uint256 amountIn, 
        uint256 reserveIn, 
        uint256 reserveOut
    ) public pure returns (uint256) {
        // Basic validations
        require(amountIn > 0, "Invalid input amount");
        require(reserveIn > 0 && reserveOut > 0, "Invalid reserves");
        require(amountIn <= reserveIn, "Swap amount exceeds reserves");
        require(amountIn >= MIN_SWAP_AMOUNT, "Amount below minimum");

        // Check for potential overflow - simplified
        require(
            amountIn <= type(uint256).max / 997,
            "Amount too large for fee calculation"
        );

        // Calculate with fee (0.3% fee = 997/1000)
        uint256 amountInWithFee = amountIn * 997;

        // Calculate output amount using constant product formula
        uint256 numerator = amountInWithFee * reserveOut;
        uint256 denominator = (reserveIn * 1000) + amountInWithFee;

        // Calculate output with minimum check
        uint256 outputAmount = numerator / denominator;
        require(
            outputAmount >= MIN_OUTPUT_AMOUNT,
            "Swap output below minimum"
        );

        return outputAmount;
    }

    /**
     * @dev View function to simulate swap output with fees
     * @param amountIn Amount of input tokens
     * @param path Array of token addresses representing swap path
     * @return uint256 Expected output amount after fees
     */
    function getAmountOutWithFees(
        uint256 amountIn,
        address[] calldata path
    ) external view returns (uint256) {
        require(path.length >= 2, "Invalid path length");
        uint256 amount = amountIn;

        // Loop through path to calculate final amount
        for (uint i; i < path.length - 1; i++) {
            (uint256 reserveIn, uint256 reserveOut) = liquidityManager.getReserves(path[i+1]);
            amount = getAmountOut(amount, reserveIn, reserveOut);
        }

        // Calculate and subtract swap fee
        uint256 fee = (amount * swapFee) / 10000;
        return amount - fee;
    }

    /**
     * @dev Withdraws stored CORAL fees from the contract
     * @param token The token address to withdraw fees from
     * @param amount The amount of fees to withdraw
     */
    function withdrawStoredFees(
        address token, 
        uint256 amount
    ) external onlyOwner {
        // Validate inputs
        require(token != address(0), "Invalid token address");
        require(amount > 0, "Invalid withdrawal amount");
        require(storedFees[token] >= amount, "Insufficient stored fees");
        require(amount <= MAX_FEE_STORAGE, "Amount exceeds maximum");

        // Update state before transfer (CEI pattern)
        storedFees[token] -= amount;

        // Handle transfer based on token type
        if (isNativeCoral(token)) {
            require(address(this).balance >= amount, ERR_INSUFFICIENT_CORAL);
            (bool success,) = payable(msg.sender).call{value: amount}("");
            require(success, "CORAL transfer failed");
        } else {
            require(
                IERC20(token).balanceOf(address(this)) >= amount,
                ERR_INSUFFICIENT_CORAL
            );
            IERC20(token).safeTransfer(msg.sender, amount);
        }

        // Emit event with timestamp for better tracking
        emit CoralFeesWithdrawn(
            token,
            amount,
            block.timestamp
        );
    }

    /**
     * @dev Updates the maximum allowed slippage with optimizations
     * @param newMaxSlippage New maximum slippage in basis points (e.g., 1000 = 10%)
     */
    function updateMaxSlippage(uint256 newMaxSlippage) external onlyOwner {
        // Check if value is actually changing
        require(newMaxSlippage != ABSOLUTE_MAX_SLIPPAGE, ERR_SAME_VALUE);

        // Validate bounds
        require(newMaxSlippage >= DEFAULT_MAX_SLIPPAGE, ERR_LOW_SLIPPAGE);
        require(newMaxSlippage <= MAXIMUM_ALLOWED_SLIPPAGE, ERR_HIGH_SLIPPAGE);

        // Store old value for event
        uint256 oldSlippage = ABSOLUTE_MAX_SLIPPAGE;

        // Update slippage
        ABSOLUTE_MAX_SLIPPAGE = newMaxSlippage;

        // Emit event with old and new values
        emit MaxSlippageUpdated(
            oldSlippage,
            newMaxSlippage,
            msg.sender
        );
    }

    /**
     * @dev View function to validate potential slippage value
     * @param slippage Slippage value to validate
     * @return bool Valid or not
     * @return string Memory reason if invalid
     */
    function validateSlippage(
        uint256 slippage
    ) external view returns (bool, string memory) {
        if (slippage == ABSOLUTE_MAX_SLIPPAGE) return (false, ERR_SAME_VALUE);
        if (slippage < DEFAULT_MAX_SLIPPAGE) return (false, ERR_LOW_SLIPPAGE);
        if (slippage > MAXIMUM_ALLOWED_SLIPPAGE) return (false, ERR_HIGH_SLIPPAGE);
        return (true, "");
    }

    /**
     * @dev View function to get current slippage settings
     */
    function getSlippageSettings() external view returns (
        uint256 defaultSlippage,
        uint256 currentMaxSlippage,
        uint256 absoluteMaximum
    ) {
        return (
            DEFAULT_MAX_SLIPPAGE,
            ABSOLUTE_MAX_SLIPPAGE,
            MAXIMUM_ALLOWED_SLIPPAGE
        );
    }

    /**
     * @dev Checks if a token is native CORAL token and has balance
     * @param token The token address to check
     * @return bool True if token is native CORAL and has balance
     */
    function isNativeCoral(address token) public pure returns (bool) {
        return false; // CORAL is always ERC20
    }

    /**
     * @dev Checks if the contract has sufficient token balance
     * @param token The token address to check
     * @param amount The amount needed
     * @return bool True if contract has sufficient balance
     */
    function hasTokenBalance(address token, uint256 amount) public view returns (bool) {
        return IERC20(token).balanceOf(address(this)) >= amount;
    }

    /**
     * @dev Safely transfers CORAL tokens to recipient
     * @param to Recipient address
     * @param amount Amount to transfer
     * @return bool True if transfer was successful
     */
    function safeTransferCoral(address to, uint256 amount) internal returns (bool) {
        require(to != address(0), "Invalid recipient");
        require(amount > 0, "Invalid amount");

        uint256 balance = IERC20(coralToken).balanceOf(address(this));
        require(balance >= amount, ERR_INSUFFICIENT_CORAL);

        IERC20(coralToken).safeTransfer(to, amount);

        emit CoralTransferred(to, amount, block.timestamp);
        return true;
    }

    // Add event for CORAL transfers
    event CoralTransferred(
        address indexed to,
        uint256 amount,
        uint256 timestamp
    );

    // Update fee storage logic with improved event emission
    function storeFees(address token, uint256 fee) internal {
        require(
            storedFees[token] + fee <= MAX_FEE_STORAGE,
            "Fee storage limit reached"
        );

        storedFees[token] += fee;

        if (storedFees[token] >= AUTO_REDEPLOY_THRESHOLD) {
            uint256 currentFees = storedFees[token];
            uint256 preRedeployBalance = storedFees[token];

            try liquidityManager.autoRedeployFees(token) {
                uint256 remainingFees = storedFees[token];
                if (remainingFees == 0) {
                    emit CoralFeesRedeployed(token, currentFees);
                } else {
                    // Emit partial redeployment events
                    uint256 redeployedAmount = preRedeployBalance - remainingFees;
                    if (redeployedAmount > 0) {
                        emit CoralFeesRedeployed(token, redeployedAmount);
                    }
                    emit CoralAutoRedeploymentFailed(token, remainingFees);
                }
            } catch {
                emit CoralAutoRedeploymentFailed(token, currentFees);
            }
        }
    }

    /**
     * @dev View function to check withdrawable fees
     * @param token The token address to check
     * @return uint256 Amount of withdrawable fees
     */
    function getWithdrawableFees(
        address token
    ) external view returns (uint256) {
        return storedFees[token];
    }

    /**
     * @dev Ensures the contract has enough CORAL tokens for a swap
     * @param amount Amount of CORAL tokens needed
     */
    function ensureCoralBalance(uint256 amount) internal {
        if (isNativeCoral(coralToken)) {
            require(address(this).balance >= amount, ERR_INSUFFICIENT_CORAL);
            return;
        }

        uint256 dexBalance = IERC20(coralToken).balanceOf(address(this));
        if (dexBalance >= amount) return;

        uint256 needed = amount - dexBalance;
        uint256 lmBalance = IERC20(coralToken).balanceOf(address(liquidityManager));
        require(lmBalance >= needed, ERR_INSUFFICIENT_CORAL);

        liquidityManager.transferCoralToDEX(needed);
        require(
            IERC20(coralToken).balanceOf(address(this)) >= amount,
            ERR_FAILED_CORAL_TRANSFER
        );
    }

    /**
     * @dev View function to check if fees need redeployment
     * @param token The token to check
     * @return needsRedeploy True if fees exceed threshold
     * @return amount Amount that can be redeployed
     */
    function checkFeesForRedeployment(
        address token
    ) external view returns (
        bool needsRedeploy,
        uint256 amount
    ) {
        uint256 currentFees = storedFees[token];
        needsRedeploy = currentFees >= AUTO_REDEPLOY_THRESHOLD;
        amount = needsRedeploy ? currentFees : 0;
    }

    /**
     * @dev Checks if a swap amount is valid
     * @param amount Amount to validate
     * @return bool True if amount is valid for swapping
     */
    function isValidSwapAmount(uint256 amount) public pure returns (bool) {
        return amount >= MIN_SWAP_AMOUNT && 
               amount <= MAX_SINGLE_SWAP;
    }

    // Add event for fee updates
    event SwapFeeUpdated(
        uint256 oldFee,
        uint256 newFee,
        address indexed updater
    );

    /**
     * @dev Swaps CORAL tokens for Creator tokens
     */
    function swapCoralForCreatorToken(
        address creatorToken,
        uint256 amountIn,
        uint256 minAmountOut,
        uint256 maxSlippage
    ) external returns (uint256) {
        // Anti-bot check
        require(block.timestamp >= lastSwapTimestamp[msg.sender] + MIN_TIME_BETWEEN_SWAPS, "Too many swaps");

        // Transfer CORAL tokens to this contract
        IERC20(coralToken).safeTransferFrom(msg.sender, address(this), amountIn);

        // Validate and set slippage
        require(maxSlippage <= ABSOLUTE_MAX_SLIPPAGE, "Slippage too high");
        uint256 slippageUsed = maxSlippage > 0 ? maxSlippage : DEFAULT_MAX_SLIPPAGE;
        require(slippageUsed >= DEFAULT_MAX_SLIPPAGE, "Invalid slippage");

        // Get reserves
        (uint256 reserveCreator, uint256 reserveCoral) = liquidityManager.getReserves(creatorToken);

        // Check liquidity
        require(reserveCreator > 0 && reserveCoral > 0, "Insufficient liquidity");
        require(amountIn <= reserveCoral * MAX_SWAP_AMOUNT_PERCENT / 100, "Swap too large");

        // Calculate amounts
        uint256 amountOut = getAmountOut(amountIn, reserveCoral, reserveCreator);
        require(amountOut >= minAmountOut, "Insufficient output amount");

        // Calculate and store fee
        uint256 fee = (amountOut * swapFee) / 10000;
        uint256 amountOutAfterFee = amountOut - fee;
        storeFees(creatorToken, fee);

        // Update last swap timestamp
        lastSwapTimestamp[msg.sender] = block.timestamp;

        // Transfer creator tokens to user
        IERC20(creatorToken).safeTransfer(msg.sender, amountOutAfterFee);

        emit CoralSwapExecuted(msg.sender, creatorToken, amountIn, amountOutAfterFee, fee);

        return amountOutAfterFee;
    }
}

contracts/SaraLiquidityManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract SaraLiquidityManager is Ownable, AccessControl {
    using SafeERC20 for IERC20;

    mapping(address => uint256) public creatorTokenReserves;
    mapping(address => uint256) public coralReserves;
    mapping(address => uint256) public collectedFees;

    address public coralToken;

    uint256 public constant REBALANCE_INTERVAL = 2 minutes;
    uint256 public lastRebalanceTimestamp;
    uint256 public constant ENGAGEMENT_THRESHOLD = 1000; // 10% in basis points

    struct EngagementMetrics {
        uint256 lastSubscriberCount;
        uint256 smoothedSubscriberCount;
        uint256 lastUpdateTime;
        uint256 updateCount;
    }

    mapping(address => EngagementMetrics) public tokenEngagement;

    // Add constant for minimum liquidity
    uint256 public constant MIN_LIQUIDITY = 10 * 1e18; // 10 CORAL minimum liquidity

    // Add new event
    event FeesRedeployed(
        address indexed token,
        uint256 amount,
        address indexed targetPool
    );

    // Add minimum fee threshold for redeployment
    uint256 public constant MIN_FEES_FOR_REDEPLOY = 5 * 1e18; // 5 CORAL minimum fees

    // Add new struct for token price discovery
    struct PriceDiscoveryData {
        uint256 initialSubscribers;
        uint256 currentSubscribers;
        uint256 observationStartTime;
        bool isInDiscovery;
        uint256[] engagementSnapshots;
    }

    // Add mapping for price discovery
    mapping(address => PriceDiscoveryData) public priceDiscovery;

    // Constants for price discovery
    uint256 public constant DISCOVERY_PERIOD = 2 minutes;
    uint256 public constant SNAPSHOT_INTERVAL = 20 seconds;
    uint256 public constant MAX_SNAPSHOTS = 6; // 2 minutes / 20 seconds

    // Events
    event PriceDiscoveryStarted(address indexed token, uint256 initialSubscribers);
    event PriceDiscoveryCompleted(
        address indexed token, 
        uint256 initialCoralPrice,
        uint256 initialLiquidity
    );
    event EngagementSnapshotTaken(
        address indexed token,
        uint256 subscribers,
        uint256 timestamp
    );

    // Add array to track all pools
    address[] public trackedPools;
    mapping(address => bool) public isTrackedPool;

    // Add event for pool tracking
    event PoolTracked(address indexed pool);
    event PoolUntracked(address indexed pool);

    // Add constant for minimum token price
    uint256 public constant MIN_TOKEN_PRICE = 0.1e18; // 0.1 CORAL minimum price

    // Add price protection constants
    uint256 public constant MAX_PRICE_MULTIPLIER = 3; // 3x cap on price increase
    uint256 public constant BASE_PRICE = 1e18; // 1 S token base price

    // Add new event for auto fee redeployment
    event AutoFeesRedeployed(
        address indexed token,
        uint256 amount,
        address indexed targetPool,
        uint256 coralAmount,
        uint256 timestamp
    );

    // Add constants for smoothing
    uint256 private constant WEIGHT_PREVIOUS = 80;
    uint256 private constant WEIGHT_NEW = 20;
    uint256 private constant REBALANCE_THRESHOLD = 110; // 10% above smoothed value
    uint256 private constant MIN_UPDATES_BEFORE_REBALANCE = 3;

    // Add event for smoothed metrics
    event EngagementSmoothed(
        address indexed token,
        uint256 rawCount,
        uint256 smoothedCount,
        uint256 timestamp
    );

    // Add trading activity tracking
    struct PoolActivity {
        uint256 lastTradeTimestamp;
        uint256 tradingVolume24h;
        uint256 lastVolumeUpdateTime;
    }

    mapping(address => PoolActivity) public poolActivity;

    // Add constants for activity checks
    uint256 public constant ACTIVITY_THRESHOLD = 7 days;
    uint256 public constant MIN_24H_VOLUME = 10 * 1e18; // 10 CORAL minimum volume

    // Add event for activity updates
    event PoolActivityUpdated(
        address indexed pool,
        uint256 volume24h,
        uint256 timestamp
    );

    // Add error messages as constants
    string private constant ERR_NO_FEES = "No fees available to redeploy";
    string private constant ERR_BELOW_MIN = "Below minimum fee threshold";
    string private constant ERR_INSUFFICIENT_CORAL = "Insufficient CORAL token balance";
    string private constant ERR_BELOW_MIN_CORAL = "Below minimum CORAL token liquidity";

    // Add DEX reference
    address public dex;

    // Add role for DEX
    bytes32 public constant DEX_ROLE = keccak256("DEX_ROLE");

    // Add state variables for tracking most needy pool
    struct PoolNeedInfo {
        address pool;
        uint256 score;
        uint256 lastUpdateTime;
    }

    PoolNeedInfo public mostNeededPool;
    uint256 public constant POOL_NEED_UPDATE_INTERVAL = 1 hours;

    // Add event for pool need updates
    event PoolNeedUpdated(
        address indexed pool,
        uint256 score,
        uint256 timestamp
    );

    // Add constant for maximum liquidity per pool
    uint256 public maxLiquidityPerPool = 10_000 * 1e18; // Initial value: 10,000 CORAL per pool

    // Add constant for minimum deployment amount
    uint256 public constant MIN_DEPLOYMENT_AMOUNT = 1 * 1e18; // 1 CORAL minimum deployment

    // Add event for max liquidity updates
    event MaxLiquidityUpdated(
        uint256 oldMax,
        uint256 newMax,
        uint256 timestamp
    );

    constructor(address _coralToken) Ownable(msg.sender) {
        coralToken = _coralToken;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    event LiquidityAdded(
        address indexed creatorToken, 
        uint256 amountCreator, 
        uint256 amountCoral
    );
    event LiquidityRemoved(
        address indexed creatorToken, 
        uint256 amountCreator, 
        uint256 amountCoral
    );
    event FeesCollected(address indexed token, uint256 amount);
    event LiquidityRebalanced(
        address indexed token, 
        uint256 newReserveCoral
    );
    event FeesWithdrawn(address indexed token, uint256 amount);

    // Add function to track new pool
    function addPoolToTracking(address pool) internal {
        if (!isTrackedPool[pool]) {
            trackedPools.push(pool);
            isTrackedPool[pool] = true;
            emit PoolTracked(pool);
        }
    }

    /**
     * @dev Checks if a token is native CORAL token
     * @param token The token address to check
     * @return bool Always returns false since CORAL is ERC-20
     */
    function isNativeCoral(address token) public pure returns (bool) {
        return false; // CORAL is ERC-20, so never treat it as native
    }

    /**
     * @dev Adds liquidity to a pool with proper validation
     * @param creatorToken The creator token address
     * @param amountCreator Amount of creator tokens to add
     * @param amountCoral Amount of CORAL tokens to add
     */
    function addLiquidity(
        address creatorToken, 
        uint256 amountCreator, 
        uint256 amountCoral
    ) external {
        require(amountCreator > 0 && amountCoral > 0, "Invalid liquidity amounts");
        require(creatorToken != address(0), "Invalid creator token");

        // Check if this is the first liquidity addition
        bool isInitialLiquidity = creatorTokenReserves[creatorToken] == 0 && 
                                 coralReserves[creatorToken] == 0;

        // Enforce minimum liquidity for subsequent additions
        if (!isInitialLiquidity) {
            require(
                creatorTokenReserves[creatorToken] + amountCreator >= MIN_LIQUIDITY &&
                coralReserves[creatorToken] + amountCoral >= MIN_LIQUIDITY,
                "Below minimum liquidity"
            );
        }

        // ✅ Add maximum liquidity check
        require(
            creatorTokenReserves[creatorToken] + amountCreator <= maxLiquidityPerPool &&
            coralReserves[creatorToken] + amountCoral <= maxLiquidityPerPool,
            "Exceeds maximum liquidity per pool"
        );

        // Transfer creator tokens to this contract
        IERC20(creatorToken).safeTransferFrom(msg.sender, address(this), amountCreator);

        // Transfer CORAL tokens (always ERC20)
        IERC20(coralToken).safeTransferFrom(msg.sender, address(this), amountCoral);

        // Update reserves
        creatorTokenReserves[creatorToken] += amountCreator;
        coralReserves[creatorToken] += amountCoral;

        // Add pool to tracking if needed
        addPoolToTracking(creatorToken);

        emit LiquidityAdded(creatorToken, amountCreator, amountCoral);
    }

    // Update removeLiquidity to handle CORAL as ERC20
    function removeLiquidity(
        address creatorToken, 
        uint256 amountCreator, 
        uint256 amountCoral
    ) external onlyOwner {
        // Basic checks
        require(creatorTokenReserves[creatorToken] >= amountCreator, "Insufficient creator liquidity");
        require(coralReserves[creatorToken] >= amountCoral, "Insufficient CORAL liquidity");

        // Add minimum liquidity check
        require(
            creatorTokenReserves[creatorToken] - amountCreator >= MIN_LIQUIDITY,
            "Cannot remove all liquidity"
        );
        require(
            coralReserves[creatorToken] - amountCoral >= MIN_LIQUIDITY,
            "Cannot remove all liquidity"
        );

        // Effects - Update reserves first
        creatorTokenReserves[creatorToken] -= amountCreator;
        coralReserves[creatorToken] -= amountCoral;

        // Interactions - Handle transfers
        IERC20(creatorToken).safeTransfer(msg.sender, amountCreator);
        IERC20(coralToken).safeTransfer(msg.sender, amountCoral);

        emit LiquidityRemoved(creatorToken, amountCreator, amountCoral);
    }

    function collectFees(address token, uint256 amount) external {
        collectedFees[token] += amount;
        emit FeesCollected(token, amount);
    }

    /**
     * @dev Returns the current reserves for a creator token pair
     * @param creatorToken The address of the creator token
     * @return creatorReserve The amount of creator tokens in reserve
     * @return coralReserve The amount of CORAL tokens in reserve
     * @notice Returns (0,0) for untracked pools instead of reverting
     */
    function getReserves(
        address creatorToken
    ) external view returns (
        uint256 creatorReserve,
        uint256 coralReserve
    ) {
        require(creatorToken != address(0), "Invalid creator token address");

        // Return zero reserves for untracked pools
        if (!isTrackedPool[creatorToken]) {
            return (0, 0);
        }

        creatorReserve = creatorTokenReserves[creatorToken];
        coralReserve = coralReserves[creatorToken];

        return (creatorReserve, coralReserve);
    }

    /**
     * @dev View function to check if a pool has valid liquidity
     * @param creatorToken The creator token to check
     * @return bool True if pool has valid liquidity
     */
    function hasValidLiquidity(
        address creatorToken
    ) public view returns (bool) {
        (uint256 creator, uint256 coral) = this.getReserves(creatorToken);
        return creator >= MIN_LIQUIDITY && 
               creator <= maxLiquidityPerPool &&
               coral >= MIN_LIQUIDITY &&
               coral <= maxLiquidityPerPool;
    }

    /**
     * @dev Withdraws collected fees for a specific token
     * @param token The ERC20 token address to withdraw fees for
     * @param amount Amount of fees to withdraw
     */
    function withdrawFees(address token, uint256 amount) external onlyOwner {
        // Input validation
        require(token != address(0), "Invalid token address");
        require(amount > 0, "Amount must be greater than zero");
        require(collectedFees[token] >= amount, "Insufficient fees");

        // Effects - Update state before transfer
        collectedFees[token] -= amount;

        // Get token contract
        IERC20 tokenContract = IERC20(token);

        // Verify contract has sufficient balance
        uint256 balance = tokenContract.balanceOf(address(this));
        require(balance >= amount, "Insufficient token balance");

        // Interactions - Transfer tokens
        tokenContract.safeTransfer(msg.sender, amount);

        emit FeesWithdrawn(token, amount);
    }

    /**
     * @dev Calculates smoothed engagement using weighted moving average
     */
    function calculateSmoothedEngagement(
        uint256 previous,
        uint256 current
    ) internal pure returns (uint256) {
        return (
            (previous * WEIGHT_PREVIOUS / 100) + 
            (current * WEIGHT_NEW / 100)
        );
    }

    /**
     * @dev Updates engagement metrics with smoothing
     */
    function updateEngagementMetrics(
        address token, 
        uint256 newSubscriberCount
    ) external onlyOwner {
        EngagementMetrics storage metrics = tokenEngagement[token];

        // Initialize if first update
        if (metrics.lastSubscriberCount == 0) {
            metrics.lastSubscriberCount = newSubscriberCount;
            metrics.smoothedSubscriberCount = newSubscriberCount;
            metrics.updateCount = 1;
            return;
        }

        // Calculate smoothed value
        uint256 newSmoothedCount = calculateSmoothedEngagement(
            metrics.smoothedSubscriberCount,
            newSubscriberCount
        );

        // Update metrics
        metrics.lastSubscriberCount = newSubscriberCount;
        metrics.smoothedSubscriberCount = newSmoothedCount;
        metrics.lastUpdateTime = block.timestamp;
        metrics.updateCount++;

        emit EngagementSmoothed(
            token,
            newSubscriberCount,
            newSmoothedCount,
            block.timestamp
        );

        // Check if rebalance needed (only after minimum updates)
        if (metrics.updateCount >= MIN_UPDATES_BEFORE_REBALANCE) {
            uint256 rebalanceThreshold = (metrics.smoothedSubscriberCount * REBALANCE_THRESHOLD) / 100;

            if (newSubscriberCount > rebalanceThreshold) {
                _rebalanceLiquidity(token, metrics.smoothedSubscriberCount);
            }
        }
    }

    /**
     * @dev View function to get smoothed engagement metrics
     */
    function getSmoothedMetrics(
        address token
    ) external view returns (
        uint256 lastCount,
        uint256 smoothedCount,
        uint256 updateCount,
        bool eligibleForRebalance
    ) {
        EngagementMetrics storage metrics = tokenEngagement[token];
        return (
            metrics.lastSubscriberCount,
            metrics.smoothedSubscriberCount,
            metrics.updateCount,
            metrics.updateCount >= MIN_UPDATES_BEFORE_REBALANCE
        );
    }

    /**
     * @dev Optimized rebalancing with minimum liquidity check
     */
    function _rebalanceLiquidity(address token, uint256 subscriberCount) internal {
        // Skip if pool has insufficient liquidity
        if (coralReserves[token] < MIN_LIQUIDITY) {
            emit RebalanceSkipped(token, coralReserves[token], "Insufficient liquidity");
            return;
        }

        // AI-based liquidity calculation
        uint256 newReserveCoral = calculateOptimalLiquidity(subscriberCount);

        // ✅ Cap at maximum liquidity
        if (newReserveCoral > maxLiquidityPerPool) {
            newReserveCoral = maxLiquidityPerPool;
            emit LiquidityCapped(token, newReserveCoral);
        }

        // Update liquidity
        coralReserves[token] = newReserveCoral;

        emit LiquidityRebalanced(token, newReserveCoral);
    }

    function calculateOptimalLiquidity(uint256 subscriberCount) internal pure returns (uint256) {
        // Initial simple formula, can be enhanced with AI later
        return subscriberCount * 1e18;
    }

    // Daily rebalance check
    function checkAndRebalance(address token) external {
        require(
            block.timestamp >= lastRebalanceTimestamp + REBALANCE_INTERVAL,
            "Too early for rebalance"
        );

        EngagementMetrics storage metrics = tokenEngagement[token];
        _rebalanceLiquidity(token, metrics.lastSubscriberCount);
        lastRebalanceTimestamp = block.timestamp;
    }

    /**
     * @dev Redeploys collected fees to target pool with proper token handling
     * @param token The token address whose fees to redeploy
     * @param targetPool The pool to receive the fees
     */
    function redeployFees(
        address token, 
        address targetPool
    ) public onlyOwner {
        // Check fee amount
        uint256 feeAmount = collectedFees[token];
        require(feeAmount > 0, ERR_NO_FEES);
        require(feeAmount >= MIN_FEES_FOR_REDEPLOY, ERR_BELOW_MIN);

        // Update state before transfer
        collectedFees[token] -= feeAmount;

        if (isNativeCoral(coralToken)) {
            // Handle native S token
            require(address(this).balance >= feeAmount, "Insufficient S balance");
            coralReserves[targetPool] += feeAmount;
            emit FeesRedeployed(token, feeAmount, targetPool);
        } else {
            // Handle ERC20 token
            require(IERC20(token).balanceOf(address(this)) >= feeAmount, "Insufficient token balance");
            IERC20(token).safeTransfer(targetPool, feeAmount);
            emit FeesRedeployed(token, feeAmount, targetPool);
        }
    }

    /**
     * @dev Auto redeploys fees when threshold is reached
     * @param token The ERC20 token address whose fees to redeploy
     */
    function autoRedeployFees(address token) external onlyDEX {
        // Input validation
        require(token != address(0), "Invalid token address");
        uint256 feeAmount = collectedFees[token];

        // Basic checks
        require(feeAmount > 0, ERR_NO_FEES);
        require(feeAmount >= MIN_FEES_FOR_REDEPLOY, ERR_BELOW_MIN);

        // Find optimal pool for redeployment
        address targetPool = findOptimalRedeploymentPool();
        require(targetPool != address(0), "No suitable pool found");

        // Calculate optimal deployment amount
        uint256 deployAmount = calculateOptimalDeployment(targetPool, feeAmount);
        require(deployAmount > 0, "No deployment needed");
        require(deployAmount <= feeAmount, "Deploy amount exceeds available fees");

        // Get token contract
        IERC20 tokenContract = IERC20(token);

        // Verify contract has sufficient balance
        uint256 balance = tokenContract.balanceOf(address(this));
        require(balance >= deployAmount, "Insufficient token balance");

        // Effects - Update state before transfer
        collectedFees[token] -= deployAmount;
        coralReserves[targetPool] += deployAmount;

        // Interactions - Transfer tokens
        tokenContract.safeTransfer(targetPool, deployAmount);

        emit AutoFeesRedeployed(
            token, 
            deployAmount, 
            targetPool, 
            deployAmount, 
            block.timestamp
        );
    }

    /**
     * @dev Optimized pool selection using cached value
     */
    function findOptimalRedeploymentPool() internal returns (address) {
        // Return cached value if recent enough
        if (
            mostNeededPool.pool != address(0) &&
            block.timestamp < mostNeededPool.lastUpdateTime + POOL_NEED_UPDATE_INTERVAL
        ) {
            // Verify pool is still valid
            if (
                isTrackedPool[mostNeededPool.pool] &&
                isPoolActive(mostNeededPool.pool)
            ) {
                return mostNeededPool.pool;
            }
        }

        // If cache is stale or invalid, update it
        updateMostNeededPool();
        return mostNeededPool.pool;
    }

    /**
     * @dev Updates the cached most needy pool
     */
    function updateMostNeededPool() internal {
        // Reset current best
        mostNeededPool.pool = address(0);
        mostNeededPool.score = 0;

        // Cache array length
        uint256 poolCount = trackedPools.length;
        if (poolCount == 0) return;

        // Cache current time
        uint256 currentTime = block.timestamp;
        uint256 activityCutoff = currentTime - ACTIVITY_THRESHOLD;

        // Single pass through pools
        for (uint256 i = 0; i < poolCount; i++) {
            address pool = trackedPools[i];

            // Skip inactive pools
            PoolActivity memory activity = poolActivity[pool];
            if (
                !isTrackedPool[pool] || 
                activity.lastTradeTimestamp < activityCutoff || 
                activity.tradingVolume24h < MIN_24H_VOLUME
            ) {
                continue;
            }

            // Calculate pool score
            uint256 score = calculatePoolScore(pool);

            // Update if better score found
            if (score > mostNeededPool.score) {
                mostNeededPool.pool = pool;
                mostNeededPool.score = score;
            }
        }

        // Update timestamp and emit event
        mostNeededPool.lastUpdateTime = currentTime;
        emit PoolNeedUpdated(
            mostNeededPool.pool,
            mostNeededPool.score,
            currentTime
        );
    }

    /**
     * @dev Force update of pool needs (public for external triggers)
     */
    function forcePoolNeedUpdate() external onlyOwner {
        updateMostNeededPool();
    }

    /**
     * @dev View function to get current pool need info
     */
    function getPoolNeedInfo() external view returns (
        address pool,
        uint256 score,
        uint256 lastUpdate,
        bool isStale
    ) {
        return (
            mostNeededPool.pool,
            mostNeededPool.score,
            mostNeededPool.lastUpdateTime,
            block.timestamp >= mostNeededPool.lastUpdateTime + POOL_NEED_UPDATE_INTERVAL
        );
    }

    /**
     * @dev Allows removal of inactive pools from tracking
     */
    function removePoolFromTracking(address pool) external onlyOwner {
        require(isTrackedPool[pool], "Pool not tracked");

        // Find and remove from array
        for (uint i = 0; i < trackedPools.length; i++) {
            if (trackedPools[i] == pool) {
                // Move last element to this position (unless we're at the end)
                if (i != trackedPools.length - 1) {
                    trackedPools[i] = trackedPools[trackedPools.length - 1];
                }
                trackedPools.pop();
                break;
            }
        }

        isTrackedPool[pool] = false;
        emit PoolUntracked(pool);
    }

    /**
     * @dev View function to get all tracked pools
     */
    function getTrackedPools() external view returns (address[] memory) {
        return trackedPools;
    }

    /**
     * @dev Starts price discovery period for a new token
     */
    function startPriceDiscovery(
        address token,
        uint256 initialSubscribers
    ) external onlyOwner {
        require(!priceDiscovery[token].isInDiscovery, "Already in discovery");

        priceDiscovery[token] = PriceDiscoveryData({
            initialSubscribers: initialSubscribers,
            currentSubscribers: initialSubscribers,
            observationStartTime: block.timestamp,
            isInDiscovery: true,
            engagementSnapshots: new uint256[](0)
        });

        emit PriceDiscoveryStarted(token, initialSubscribers);
    }

    /**
     * @dev Records engagement snapshot during discovery period
     */
    function recordEngagementSnapshot(
        address token,
        uint256 currentSubscribers
    ) external onlyOwner {
        PriceDiscoveryData storage data = priceDiscovery[token];
        require(data.isInDiscovery, "Not in discovery");
        require(
            data.engagementSnapshots.length < MAX_SNAPSHOTS,
            "Max snapshots reached"
        );

        data.engagementSnapshots.push(currentSubscribers);
        data.currentSubscribers = currentSubscribers;

        emit EngagementSnapshotTaken(token, currentSubscribers, block.timestamp);

        // If we have enough snapshots, complete discovery
        if (data.engagementSnapshots.length == MAX_SNAPSHOTS) {
            completePriceDiscovery(token);
        }
    }

    /**
     * @dev Completes price discovery and sets initial liquidity
     */
    function completePriceDiscovery(address token) public onlyOwner {
        PriceDiscoveryData storage data = priceDiscovery[token];
        require(data.isInDiscovery, "Not in discovery");
        require(
            block.timestamp >= data.observationStartTime + DISCOVERY_PERIOD,
            "Discovery period not complete"
        );

        // Calculate optimal initial price based on engagement trends
        uint256 initialPrice = calculateInitialPrice(token);
        uint256 initialLiquidity = calculateInitialLiquidity(
            data.currentSubscribers,
            initialPrice
        );

        // Set initial liquidity
        coralReserves[token] = initialLiquidity;

        // Mark discovery as complete
        data.isInDiscovery = false;

        emit PriceDiscoveryCompleted(token, initialPrice, initialLiquidity);
    }

    /**
     * @dev Calculates initial price based on engagement trends with circuit breaker
     */
    function calculateInitialPrice(
        address token
    ) internal returns (uint256) {
        PriceDiscoveryData storage data = priceDiscovery[token];

        // Calculate engagement growth rate
        uint256 totalGrowth = 0;
        uint256 positiveSnapshots = 0;

        for (uint256 i = 0; i < data.engagementSnapshots.length; i++) {
            if (i > 0) {
                // Handle potential negative growth
                if (data.engagementSnapshots[i] > data.engagementSnapshots[i-1]) {
                    uint256 growth = ((data.engagementSnapshots[i] - data.engagementSnapshots[i-1]) * 10000) 
                                    / data.engagementSnapshots[i-1];

                    // Cap individual growth rate
                    growth = growth > 10000 ? 10000 : growth; // Cap at 100% per snapshot

                    totalGrowth += growth;
                    positiveSnapshots++;
                }
            }
        }

        // Calculate average growth, defaulting to 0 if no positive growth
        uint256 avgGrowth = positiveSnapshots > 0 ? 
            totalGrowth / positiveSnapshots : 
            0;

        // Calculate price with growth rate
        uint256 calculatedPrice = BASE_PRICE + ((BASE_PRICE * avgGrowth) / 10000);

        // Calculate maximum allowed price
        uint256 maxAllowedPrice = BASE_PRICE * MAX_PRICE_MULTIPLIER;

        // Apply circuit breaker logic
        if (calculatedPrice > maxAllowedPrice) {
            emit PriceCapReached(token, calculatedPrice, maxAllowedPrice);
            calculatedPrice = maxAllowedPrice;
        }

        return calculatedPrice > MIN_TOKEN_PRICE ? calculatedPrice : MIN_TOKEN_PRICE;
    }

    // Add event for monitoring price caps
    event PriceCapReached(
        address indexed token,
        uint256 calculatedPrice,
        uint256 cappedPrice
    );

    // Add event for fallback pool selection
    event FallbackPoolSelected(
        address indexed pool,
        uint256 reserveAmount
    );

    // Add event for skipped rebalances
    event RebalanceSkipped(
        address indexed token,
        uint256 coralReserve,
        string reason
    );

    // Enhanced pool selection event
    event PoolSelected(
        address indexed pool,
        uint256 score,
        uint256 currentLiquidity
    );

    /**
     * @dev Updates pool trading activity
     */
    function updatePoolActivity(
        address pool,
        uint256 tradeAmount
    ) external onlyDEX {
        PoolActivity storage activity = poolActivity[pool];

        // Update last trade timestamp
        activity.lastTradeTimestamp = block.timestamp;

        // Update 24h volume
        if (block.timestamp >= activity.lastVolumeUpdateTime + 24 hours) {
            // Reset volume after 24h
            activity.tradingVolume24h = tradeAmount;
            activity.lastVolumeUpdateTime = block.timestamp;
        } else {
            activity.tradingVolume24h += tradeAmount;
        }

        emit PoolActivityUpdated(
            pool,
            activity.tradingVolume24h,
            block.timestamp
        );
    }

    /**
     * @dev Checks if pool is actively traded
     */
    function isPoolActive(address pool) public view returns (bool) {
        PoolActivity storage activity = poolActivity[pool];
        uint256 lastTrade = activity.lastTradeTimestamp;
        uint256 volume = activity.tradingVolume24h;

        if (lastTrade < block.timestamp - ACTIVITY_THRESHOLD) return false;
        if (volume < MIN_24H_VOLUME) return false;
        return true;
    }

    /**
     * @dev Calculates pool score based on liquidity and activity
     */
    function calculatePoolScore(
        address pool
    ) internal view returns (uint256) {
        // Cache values to save gas
        uint256 liquidity = coralReserves[pool];
        uint256 volume = poolActivity[pool].tradingVolume24h;

        // Use bit shifts for multiplication (more gas efficient)
        // 70% = multiply by 7/10
        // 30% = multiply by 3/10
        return (
            (liquidity * 7 + volume * 3) / 10
        );
    }

    /**
     * @dev View function to get pool activity metrics
     */
    function getPoolActivity(
        address pool
    ) external view returns (
        uint256 lastTrade,
        uint256 volume24h,
        bool isActive
    ) {
        PoolActivity storage activity = poolActivity[pool];
        return (
            activity.lastTradeTimestamp,
            activity.tradingVolume24h,
            isPoolActive(pool)
        );
    }

    /**
     * @dev Calculates the optimal amount of fees to deploy to a pool
     * @param targetPool The pool to deploy fees to
     * @param availableFees Total available fees
     * @return Amount of fees to deploy
     */
    function calculateOptimalDeployment(
        address targetPool,
        uint256 availableFees
    ) internal view returns (uint256) {
        // Safety check: Ensure valid target pool
        if (targetPool == address(0)) {
            return 0;
        }

        // Ensure minimum available fees
        if (availableFees < MIN_DEPLOYMENT_AMOUNT) {
            return 0;
        }

        // Get current pool metrics
        uint256 currentLiquidity = coralReserves[targetPool];
        PoolActivity memory activity = poolActivity[targetPool];

        uint256 deployAmount;

        // Calculate deployment amount based on conditions
        if (currentLiquidity < MIN_LIQUIDITY) {
            // If pool has low liquidity, deploy all available fees
            deployAmount = availableFees;
        } else if (activity.tradingVolume24h > MIN_24H_VOLUME * 2) {
            // If pool is highly active, deploy 75% of available fees
            deployAmount = (availableFees * 75) / 100;
        } else {
            // Default case: Deploy 50% of available fees
            deployAmount = availableFees / 2;
        }

        // Ensure minimum deployment amount
        if (deployAmount < MIN_DEPLOYMENT_AMOUNT) {
            return 0; // Don't deploy if below minimum
        }

        // Cap deployment at available fees
        return deployAmount > availableFees ? availableFees : deployAmount;
    }

    /**
     * @dev Transfers CORAL tokens to the DEX contract
     * @param amount Amount of CORAL tokens to transfer
     */
    function transferCoralToDEX(uint256 amount) external onlyOwner {
        require(amount > 0, "Amount must be greater than zero");
        require(dex != address(0), "DEX address is not set");

        // Get CORAL token contract
        IERC20 coral = IERC20(coralToken);

        // Check balance
        uint256 balance = coral.balanceOf(address(this));
        require(balance >= amount, "Insufficient CORAL balance");

        // Transfer CORAL tokens using safeTransfer
        coral.safeTransfer(dex, amount);

        emit CoralTokenTransferred(dex, amount, block.timestamp);
    }

    // Update event to include timestamp
    event CoralTokenTransferred(
        address indexed to,
        uint256 amount,
        uint256 timestamp
    );

    /**
     * @dev Modifier to check if caller has DEX role
     */
    modifier onlyDEX() {
        require(hasRole(DEX_ROLE, msg.sender), "Caller does not have DEX role");
        _;
    }

    /**
     * @dev Sets a new DEX address with proper role management
     * @param _dex The new DEX address
     */
    function setDEX(address _dex) external onlyOwner {
        require(_dex != address(0), "Invalid DEX address");

        // Revoke role from old DEX if it exists
        if (dex != address(0)) {
            revokeRole(DEX_ROLE, dex);
        }

        // Grant role to new DEX
        grantRole(DEX_ROLE, _dex);

        // Update DEX address
        dex = _dex;

        emit DEXUpdated(_dex);
    }

    /**
     * @dev Allows owner to grant DEX role to additional addresses
     * @param account Address to grant DEX role to
     */
    function grantDEXRole(address account) external onlyOwner {
        require(account != address(0), "Invalid address");
        grantRole(DEX_ROLE, account);
    }

    /**
     * @dev Allows owner to revoke DEX role from addresses
     * @param account Address to revoke DEX role from
     */
    function revokeDEXRole(address account) external onlyOwner {
        require(account != address(0), "Invalid address");
        require(account != dex, "Cannot revoke from primary DEX");
        revokeRole(DEX_ROLE, account);
    }

    // Add event for DEX updates
    event DEXUpdated(address indexed newDEX);

    // Add event for liquidity capping
    event LiquidityCapped(
        address indexed token,
        uint256 cappedAmount
    );

    /**
     * @dev Calculates initial liquidity based on subscriber count and CORAL price
     * @param subscriberCount Current number of subscribers
     * @param price Initial price in CORAL tokens (18 decimals)
     * @return uint256 Initial liquidity amount in CORAL tokens
     */
    function calculateInitialLiquidity(
        uint256 subscriberCount,
        uint256 price
    ) public view returns (uint256) {
        // Input validation
        require(subscriberCount > 0, "Invalid subscriber count");
        require(price >= MIN_TOKEN_PRICE, "Price below minimum");
        require(price <= BASE_PRICE * MAX_PRICE_MULTIPLIER, "Price above maximum");

        // Check for multiplication overflow before performing calculation
        require(subscriberCount <= type(uint256).max / price, "Multiplication overflow");

        // Calculate initial liquidity
        uint256 liquidity = subscriberCount * price;

        // Validate liquidity bounds
        require(liquidity >= MIN_LIQUIDITY, "Liquidity below minimum");
        require(liquidity <= maxLiquidityPerPool, "Liquidity above maximum");

        return liquidity;
    }

    /**
     * @dev Helper function to validate if a liquidity amount is within acceptable bounds
     * @param liquidity Amount to validate
     * @return bool True if liquidity is valid
     */
    function isValidLiquidity(uint256 liquidity) public view returns (bool) {
        return liquidity >= MIN_LIQUIDITY && 
               liquidity <= maxLiquidityPerPool;
    }

    // Add event for skipped deployments
    event DeploymentSkipped(
        address indexed targetPool,
        uint256 calculatedAmount,
        string reason
    );

    /**
     * @dev Allows owner to update maximum liquidity per pool
     * @param _newMax New maximum liquidity value
     */
    function setMaxLiquidity(uint256 _newMax) external onlyOwner {
        // Input validation
        require(_newMax >= MIN_LIQUIDITY, "New max must be >= min liquidity");
        require(_newMax <= type(uint256).max / MAX_PRICE_MULTIPLIER, "Max too high");

        // Store old value for event
        uint256 oldMax = maxLiquidityPerPool;

        // Update max liquidity
        maxLiquidityPerPool = _newMax;

        // Emit event
        emit MaxLiquidityUpdated(oldMax, _newMax, block.timestamp);
    }

    /**
     * @dev View function to get current liquidity limits
     * @return min The minimum liquidity required
     * @return max The maximum liquidity allowed
     */
    function getLiquidityLimits() external view returns (uint256 min, uint256 max) {
        return (MIN_LIQUIDITY, maxLiquidityPerPool);
    }

}

contracts/SaraTokenRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./SaraDEX.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
// Add CreatorCoin interface with the methods we need
interface ICreatorCoin {
    function mint(address to, uint256 amount) external;
    function burn(address from, uint256 amount) external;
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
}

contract SaraTokenRouter is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // Add withdrawal reason tracking
    enum WithdrawReason { ADMIN_OVERRIDE, MIGRATION, CONTRACT_ERROR }

    address public coralToken;
    SaraLiquidityManager public liquidityManager;
    SaraDEX public dex;
    mapping(address => bool) public listedTokens;
    ICreatorCoin public creatorTokenFactory;  // Use interface instead of concrete implementation
    bool public paused;
    uint256 public constant MIN_INITIAL_CORAL = 1000 * 1e18;  // 1000 CORAL tokens
    uint256 public constant MAX_INITIAL_CORAL = 1000000 * 1e18;  // 1M CORAL tokens

    // Add mapping to track tokens in price discovery
    mapping(address => bool) public inPriceDiscovery;

    // Add error messages as constants for consistency
    string private constant ERR_ZERO_CORAL = "Initial liquidity cannot be zero";
    string private constant ERR_ZERO_SUBSCRIBERS = "Initial subscribers cannot be zero";
    string private constant ERR_INSUFFICIENT_CORAL = "Insufficient CORAL token balance";
    string private constant ERR_INVALID_CORAL_AMOUNT = "Invalid CORAL token amount";
    string private constant ERR_INVALID_CREATOR = "Invalid creator token";

    // Remove approval tracking for CORAL tokens since they're native
    mapping(address => bool) public hasOpenApproval;
    uint256 public constant APPROVAL_TIMEOUT = 1 hours;
    uint256 public lastApprovalTimestamp;

    // Add pause state tracking
    uint256 public lastPauseChange;

    event TokenListed(address indexed creatorToken);
    event TokenAutoListed(address indexed token, uint256 initialLiquidity);
    event ApprovalGranted(address indexed token, uint256 amount, uint256 timestamp);
    event ApprovalRevoked(address indexed token, uint256 timestamp);
    event PauseStateChanged(
        bool indexed isPaused,
        uint256 timestamp,
        address indexed actor
    );
    event TokenSwappedForCoral(
        address indexed token,
        uint256 amountIn,
        uint256 amountOut
    );
    event CoralSwappedForToken(
        address indexed token,
        uint256 amountIn,
        uint256 amountOut
    );
    event PriceDiscoveryFailed(address indexed token, uint256 timestamp);
    event PriceDiscoveryCompleted(address indexed token, uint256 timestamp);
    event EmergencyWithdraw(
        address indexed token,
        address indexed to,
        uint256 amount,
        uint256 timestamp,
        WithdrawReason reason
    );

    modifier whenNotPaused() {
        require(!paused, "Contract is paused");
        _;
    }

    constructor(
        address _coralToken,
        address _liquidityManager,
        address _dex,
        address _creatorTokenFactory
    ) Ownable(msg.sender) {
        coralToken = _coralToken;
        liquidityManager = SaraLiquidityManager(payable(_liquidityManager));
        dex = SaraDEX(payable(_dex));
        creatorTokenFactory = ICreatorCoin(_creatorTokenFactory);
    }

    /**
     * @dev Lists a new creator token in the DEX
     */
    function listNewCreatorToken(address creatorToken) public onlyOwner {
        require(!listedTokens[creatorToken], "Token already listed");
        listedTokens[creatorToken] = true;
        emit TokenListed(creatorToken);
    }

    /**
     * @dev Updates AI rebalance through liquidity manager
     */
    function updateAIRebalance(
        address creatorToken, 
        uint256 newReserveS
    ) external onlyOwner {
        // Direct call to liquidity manager
        liquidityManager.addLiquidity(creatorToken, 0, newReserveS);
    }

    /**
     * @dev Toggles pause state with simplified logic
     */
    function togglePause() external onlyOwner {
        paused = !paused;
        lastPauseChange = block.timestamp;

        emit PauseStateChanged(
            paused,
            block.timestamp,
            msg.sender
        );
    }

    /**
     * @dev View function to get pause state details
     */
    function getPauseState() external view returns (
        bool isPaused,
        uint256 lastChange,
        uint256 duration
    ) {
        return (
            paused,
            lastPauseChange,
            lastPauseChange > 0 ? block.timestamp - lastPauseChange : 0
        );
    }

    /**
     * @dev Safely manages token approvals with optimized approval handling
     */
    function manageApproval(address token, uint256 amount) internal {
        if (token == coralToken) return;

        uint256 currentAllowance = IERC20(token).allowance(address(this), address(liquidityManager));

        if (hasOpenApproval[token] && currentAllowance > 0) {
            IERC20(token).approve(address(liquidityManager), 0);  // Use regular approve for resetting
            hasOpenApproval[token] = false;
            emit ApprovalRevoked(token, block.timestamp);
        }

        if (currentAllowance < amount) {
            if (currentAllowance > 0) {
                IERC20(token).approve(address(liquidityManager), 0);  // Use regular approve for resetting
            }
            IERC20(token).approve(address(liquidityManager), amount);  // Use regular approve for setting
            hasOpenApproval[token] = true;
            lastApprovalTimestamp = block.timestamp;
            emit ApprovalGranted(token, amount, block.timestamp);
        }
    }

    /**
     * @dev Auto-revokes expired approvals for creator tokens
     */
    function checkAndRevokeExpiredApprovals(address token) internal {
        // Skip if it's the CORAL token
        if (token == coralToken) return;

        if (hasOpenApproval[token] && 
            block.timestamp > lastApprovalTimestamp + APPROVAL_TIMEOUT) {
            revokeApproval(token);
        }
    }

    /**
     * @dev Explicit approval revocation with optimization
     */
    function revokeApproval(address token) public onlyOwner {
        // Skip if it's the CORAL token
        if (token == coralToken) return;

        // Check current allowance first
        uint256 currentAllowance = IERC20(token).allowance(
            address(this), 
            address(liquidityManager)
        );

        // Only revoke if there's an allowance
        if (currentAllowance > 0 && hasOpenApproval[token]) {
            IERC20(token).approve(address(liquidityManager), 0);
            hasOpenApproval[token] = false;
            emit ApprovalRevoked(token, block.timestamp);
        }
    }

    /**
     * @dev Called when a new token is minted
     */
    function onTokenMinted(
        address token, 
        uint256 subscriberCount
    ) external whenNotPaused {
        // Basic checks
        require(msg.sender == address(creatorTokenFactory), "Unauthorized");
        require(!inPriceDiscovery[token], "Token already in discovery");
        require(subscriberCount > 0, ERR_ZERO_SUBSCRIBERS);

        // Calculate initial liquidity
        uint256 initialLiquidity = calculateInitialLiquidity(subscriberCount);
        require(initialLiquidity > 0, ERR_ZERO_CORAL);

        // Check allowance before transfer
        require(
            IERC20(coralToken).allowance(msg.sender, address(this)) >= initialLiquidity,
            "Insufficient CORAL allowance"
        );

        // Transfer CORAL tokens from sender
        IERC20(coralToken).safeTransferFrom(msg.sender, address(this), initialLiquidity);

        // Mark token as in price discovery before external calls
        inPriceDiscovery[token] = true;

        // Direct call to liquidityManager
        liquidityManager.startPriceDiscovery(token, subscriberCount);

        // List token (but don't add liquidity yet)
        listNewCreatorToken(token);

        emit TokenAutoListed(token, initialLiquidity);
    }

    /**
     * @dev Updates engagement metrics during price discovery
     */
    function updateEngagementMetrics(
        address token,
        uint256 currentSubscribers
    ) external onlyOwner {
        require(inPriceDiscovery[token], "Token not in discovery");
        liquidityManager.recordEngagementSnapshot(token, currentSubscribers);
    }

    /**
     * @dev Completes price discovery with improved validation and re-entrancy protection
     */
    function completePriceDiscovery(
        address token
    ) external onlyOwner nonReentrant {
        require(inPriceDiscovery[token], "Token not in discovery");

        uint256 currentLiquidity = IERC20(coralToken).balanceOf(address(this));
        require(currentLiquidity > 0, ERR_ZERO_CORAL);

        uint256 initialReserve;
        (, initialReserve) = liquidityManager.getReserves(token);

        // Update state first to prevent re-entrancy
        inPriceDiscovery[token] = false;

        // External interactions after state changes (CEI pattern)
        liquidityManager.completePriceDiscovery(token);

        (, uint256 sReserve) = liquidityManager.getReserves(token);
        require(sReserve > initialReserve, "Liquidity not added");

        emit PriceDiscoveryCompleted(token, block.timestamp);
    }

    /**
     * @dev Calculates initial liquidity based on subscriber count
     */
    function calculateInitialLiquidity(
        uint256 subscriberCount
    ) public pure returns (uint256) {
        require(subscriberCount > 0, ERR_ZERO_SUBSCRIBERS);
        return subscriberCount * 1e18;
    }

    /**
     * @dev Swaps CORAL tokens for creator tokens with optimized approval handling
     */
    function swapCoralForToken(
        address token,
        uint256 coralAmount,
        uint256 minAmountOut
    ) external whenNotPaused nonReentrant returns (uint256) {
        require(token != address(0), ERR_INVALID_CREATOR);
        require(coralAmount > 0, ERR_INVALID_CORAL_AMOUNT);
        require(!inPriceDiscovery[token], "Token in price discovery");
        require(listedTokens[token], "Token not listed");

        // Transfer CORAL tokens to contract
        IERC20(coralToken).safeTransferFrom(msg.sender, address(this), coralAmount);

        // Optimize approval handling: Only update if less than required
        uint256 currentAllowance = IERC20(coralToken).allowance(address(this), address(dex));
        if (currentAllowance < coralAmount) {
            if (currentAllowance > 0) {
                IERC20(coralToken).approve(address(dex), 0);  // Use regular approve for resetting
            }
            IERC20(coralToken).approve(address(dex), coralAmount);  // Use regular approve for setting
        }

        // Execute swap through DEX
        uint256 amountOut = dex.swapCoralForCreatorToken(
            token,
            coralAmount,
            minAmountOut,
            500 // Default max slippage of 5%
        );

        require(amountOut >= minAmountOut, "Slippage too high");

        // Emit event before transfer (CEI pattern)
        emit CoralSwappedForToken(token, coralAmount, amountOut);

        // Transfer creator tokens to user
        IERC20(token).safeTransfer(msg.sender, amountOut);

        return amountOut;
    }

    /**
     * @dev Emergency function to withdraw stuck CORAL tokens with reason tracking
     * @param to Address to send CORAL tokens to
     * @param amount Amount of CORAL tokens to withdraw
     * @param reason Reason for emergency withdrawal
     */
    function emergencyWithdrawCoral(
        address to, 
        uint256 amount,
        WithdrawReason reason
    ) external onlyOwner nonReentrant {
        require(to != address(0), "Invalid address");
        require(!paused, "Contract is paused");

        uint256 balance = IERC20(coralToken).balanceOf(address(this));
        require(amount <= balance, "Insufficient CORAL balance");

        // Emit event before transfer (CEI pattern)
        emit EmergencyWithdraw(coralToken, to, amount, block.timestamp, reason);

        // Transfer CORAL tokens
        IERC20(coralToken).safeTransfer(to, amount);
    }
}

